(24, 24, 'ar', 'سكونة-سرير', 'سكونة سرير', '<p>سكونة سرير</p>', 'سكونة سرير', 'سكونة سرير'),
(25, 25, 'ar', 'فوط-وبشاكير', 'فوط وبشاكير', '<p>فوط وبشاكير</p>', 'فوط وبشاكير', 'فوط وبشاكير'),
