@foreach($directories as $directory)  <li role="treeitem" aria-expanded="false" aria-selected="false">    <div aria-label="{{$directory['path']}}"> {{$directory['name']}}      @if(in_array($directory['path'], $db_directories) )        <button class="updatefolder addToList" id="{{$directory['path']}}"><i class="fas fa-plus"></i></button>      @else        <button class="updatefolder removeFromList" id="{{$directory['path']}}"><i class="fas fa-trash-alt"></i></button>      @endif    </div>    @if(count($directory['children']) > 0)      <ul role="group">        @include('AppPlugin.FileManager.browser_tree_folder_admin',['directories'=>$directory['children'],'db_directories'=>$db_directories] )      </ul>    @endif  </li>@endforeach